// SPDX-License-Identifier: MIT
import "./COOPToken.sol";

pragma solidity ^0.8.0;

contract Coop {

    COOPToken internal coopToken;

    address public owner;
    address public COOPTokenAddress;

    // map of cooperant addresses to structs
    mapping (address => Cooperant) cooperants;      

    struct Cooperant {
        uint256 feePayed;
        uint256 kg;
    } 

    constructor (address _contractAddress) {
       owner = msg.sender;
       COOPTokenAddress = _contractAddress;
       coopToken = COOPToken(COOPTokenAddress);
    } 

    event makeTransfer (address _sender, uint256 _amount);
    event depositFruits (address _cooperant, uint256 _kilograms);


    // modifier onlyOwner() {
    //   require(msg.sender == owner, "Not authorized.");
    //   _;
    // }

    // WIDROW FUNCTION
//     function withdraw(uint _amount) public payable onlyOwner {
//      owner.transfer(_amount);
//   }


    // Izbaciti payable zato sto ne placam nista
    function coopTokenTransfer (uint256 _kg) private returns (bool) {        
        return coopToken.transfer(owner, msg.sender, _kg);
    }  


    // fund COOP with yearly fee
    // proveriti gde ide ovaj novac, da li na racun ugovora
    function fundCoop() public payable returns (bool) {
        cooperants[msg.sender].feePayed += msg.value;
        emit makeTransfer(msg.sender, msg.value);
        return true;
    }

    // videti ovu funkciju iz bezbednosnog ugla jer ovako kako stoji prakticno mzoe bilo ko da je koristi kolko oce
    function depositFruitsToCOOP(uint256 _kg) public returns (bool) {
        cooperants[msg.sender].kg += _kg;
        coopTokenTransfer(_kg*(10**18));  
        emit depositFruits (msg.sender, _kg);
        return true;  
     }       


    // // Inherited function from COOPToken Contract
    //  function getCoopTokenUserbalance (address _userAddress) public view returns (uint) {        
    //     return coopToken.balanceOf(_userAddress)/(10**18);        
    // }  

    function getUserAccountBalance(address _salje) public view returns (uint256, uint256, uint256) {
        return (cooperants[_salje].feePayed, cooperants[_salje].kg, coopToken.balanceOf(_salje)/(10**18));
    }
}
